div.script:
	function googleTranslateElementInit() {
			new google.translate.TranslateElement({
			
			pageLanguage: 'en',
			//layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
			autoDisplay: false
		}, 'google_translate_element');
		$('.goog-te-gadget-simple').css('font-size', '10px');
		$('.goog-te-menu-frame').attr('title', 'Google Translator');
		// begin accessibility compliance, requires jQuery
        $('img.goog-te-gadget-icon').attr('alt','Google Translate');
        $('div#goog-gt-tt div.logo img').attr('alt','translate');
        $('div#goog-gt-tt .original-text').css('text-align','left');
        $('.goog-te-gadget-simple .goog-te-menu-value span').css('color','#000');
		$('.goog-te-combo').attr('title','Select A Language');
		//$("html").attr("lang", pageLanguage);
        // end accessibility compliance
	}

link.script:
    $(document).ready(function() {
      homePageFunctions();
      if ($('.white-popup').length) {
        $('.slide-pop').magnificPopup({
          disableOn: 700,
          type: 'inline',
          midClick: true,
          mainClass: 'mfp-fade',
          removalDelay: 160,
          preloader: false,
          fixedContentPos: false
        });
      }
      $('.main-slider').slick({
			  dots: true,
			  fade: true,
			  infinite: true,
			  adaptiveHeight: true, 
			  autoplay: true,
			  autoplaySpeed: '4000',
			  arrows: false,
			  appendDots: '.slickDots'
			});      
      if($('.rotating-stories').length){
        $('.rotating-stories').slick({
          dots: false,
          cssEase: 'linear',
          slidesToShow: 3,
          slidesToScroll:1,
          infinite: true,
          autoplay: true,
          autoplaySpeed: '4000',
          arrows: true,
          prevArrow: 'Previous',
          nextArrow: 'Next',
          responsive: [
            {
              breakpoint: 1025,
              settings: {
                slidesToShow: 2,
                slidesToScroll:1
              }
            },
            {
              breakpoint: 801,
              settings: {
                slidesToShow: 1,
                slidesToScroll:1
              }
            }
          ]
        });
      }
    });
