div.script:
$(document).ready(function() {
  $('#search').keydown(function (event) {
      var keypressed = event.keyCode || event.which;
      if (keypressed == 13) {
          event.preventDefault();   
          SearchSite();
          return false;
       }   
  });
  $('#searchMobile').keydown(function (event) {
      var keypressed = event.keyCode || event.which;
      if (keypressed == 13) {
          event.preventDefault();   
          $("#search").val($("#searchMobile").val());
          SearchSite();
          return false;
       }   
  });
});
function SearchSite() {
  var searchParam = $("#search").val();
  if(searchParam.length > 0) {
    window.location.href =  '/District/Search?searchtext='+encodeURI(searchParam);
  }
}

var logoCopy = $(".logo-bar img[role*='logo']");
if(logoCopy.length == 1) {
  var imageCopy = (logoCopy.parent().is("a") ? logoCopy.parent().clone() : logoCopy.clone());
  $(imageCopy).addClass("MobileLogo");
  $("#mobileLogo").append(imageCopy);
}

