	function switchDip_478036(num,pause){
		clearTimeout(slideshowTimer_478036);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_478036();
		var speed = slideshowMotionSpeed_478036();
		var currentNum = document.getElementById('currentNum_478036').value;
		var totalDips = document.getElementById('totalDips_478036').value;
		var video = document.getElementById('video_478036').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_478036(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
