			}else{
				$('#dipImage_478036' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_478036' + nums).length){
				$('#videoShell_478036' + nums).fadeOut(speed);
			}
			if($('#dipContent_478036' + nums).length){
				$('#dipContent_478036' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_478036' + nums).length){
				$('#nav_478036' + nums).addClass('on');
			}
			
			if($('#nav_li_478036' + nums).length){
				$('#nav_li_478036' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_478036' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_478036' + nums).length){
					$('#videoShell_478036' + nums).fadeIn(speed);
					}	
				});
