	function switchDip_449396(num,pause){
		clearTimeout(slideshowTimer_449396);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_449396();
		var speed = slideshowMotionSpeed_449396();
		var currentNum = document.getElementById('currentNum_449396').value;
		var totalDips = document.getElementById('totalDips_449396').value;
		var video = document.getElementById('video_449396').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_449396(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_449396' + nums).length){
				$('#nav_449396' + nums).removeClass('on');
			}
			
			if($('#nav_li_449396' + nums).length){
				$('#nav_li_449396' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_449396' + nums).length){
					$('#videoShell_449396' + nums).hide();
				}
				$('#dipImage_449396' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_449396' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_449396' + nums).length){
				$('#videoShell_449396' + nums).fadeOut(speed);
			}
			if($('#dipContent_449396' + nums).length){
				$('#dipContent_449396' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_449396' + nums).length){
				$('#nav_449396' + nums).addClass('on');
			}
			
			if($('#nav_li_449396' + nums).length){
				$('#nav_li_449396' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_449396' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_449396' + nums).length){
					$('#videoShell_449396' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_449396' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_449396' + nums).length){
					$('#videoShell_449396' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_449396' + nums).length){
				$('#dipContent_449396' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_449396(timer);
			document.getElementById('currentNum_449396').value = nums;
		}
	}
	
	function slideshowMotion_449396(){
