	function switchDip_332022(num,pause){
		clearTimeout(slideshowTimer_332022);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_332022();
		var speed = slideshowMotionSpeed_332022();
		var currentNum = document.getElementById('currentNum_332022').value;
		var totalDips = document.getElementById('totalDips_332022').value;
		var video = document.getElementById('video_332022').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_332022(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_332022' + nums).length){
				$('#nav_332022' + nums).removeClass('on');
			}
			
			if($('#nav_li_332022' + nums).length){
				$('#nav_li_332022' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_332022' + nums).length){
					$('#videoShell_332022' + nums).hide();
				}
				$('#dipImage_332022' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_332022' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_332022' + nums).length){
				$('#videoShell_332022' + nums).fadeOut(speed);
			}
			if($('#dipContent_332022' + nums).length){
				$('#dipContent_332022' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_332022' + nums).length){
				$('#nav_332022' + nums).addClass('on');
			}
			
			if($('#nav_li_332022' + nums).length){
				$('#nav_li_332022' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_332022' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_332022' + nums).length){
					$('#videoShell_332022' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_332022' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_332022' + nums).length){
					$('#videoShell_332022' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_332022' + nums).length){
				$('#dipContent_332022' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_332022(timer);
			document.getElementById('currentNum_332022').value = nums;
		}
	}
	
	function slideshowMotion_332022(){
