$(document).ready(function() {
	homePageFunctions();
	$('.main-slider').slick({
		dots: true,
		fade: true,
		infinite: true,
		adaptiveHeight: true, 
		autoplay: true,
		autoplaySpeed: '4000',
		arrows: false,
		appendDots: '.slideNav',
		pauseOnDotsHover: true
	});
	if ($('.white-popup').length) {
		$('.slide-pop').magnificPopup({
		  disableOn: 700,
		  type: 'inline',
		  midClick: true,
		  mainClass: 'mfp-fade',
		  removalDelay: 160,
		  preloader: false,
		  fixedContentPos: false
		});
	}
	
		$('.eventsSlider').slick({
			infinite: true,
			speed: 500,
			slidesToShow: 4,
			slidesToScroll: 4,
			adaptiveHeight: true,
			arrows: true,
			prevArrow: 'Previous',
			nextArrow: 'Next',
			responsive: [
				{
				  breakpoint: 1300,
				  settings: {
					slidesToShow: 3,
					slidesToScroll: 3,
					infinite: true
				  }
				},
				{
				  breakpoint: 980,
				  settings: {
					slidesToShow: 2,
					slidesToScroll: 2
				  }
				},
				{
				  breakpoint: 620,
				  settings: {
					slidesToShow: 1,
					slidesToScroll: 1
				  }
				}
			  ]
		});
	
});
