$(document).ready(function () {
    $('[data-toggle="popover"]').popover({
        trigger: 'hover',
        placement: 'bottom',
        html: true,
        title: function() {
            return $(".qtip .title", $(this)).html();
        },
        content: function() {
            return $(".qtip .content", $(this)).html();
        }
    });

    var $cal = $("#mini-cal-wrapper");

    $cal.on({
        click: function(e) {
            var $self = $(this);
            var url   = $self.attr("href");

            var updatedUrl = url.replace(/\/([\w_\-0-9]+)\/month(\/\d+\/\d+)/, "\/$1\/mini_cal$2", url);

            $.ajax({
                url: updatedUrl,
                type: "GET",
                success: function(response) {
                    $cal.html(response);
                }
            });

            e.preventDefault();
            e.stopPropagation();
            return false;
        }
    }, ".table thead a");
});

$(document).ready(function() {
    $('.new_carousel').carousel({
        interval: false
    });

    $('li').click(function() {

        // get this data-target
        var target = $(this).data("slide-to");

        $('#carouselExampleIndicators1').carousel(target);

    });

    $('#carouselExampleIndicators').on('slide.bs.carousel', function() {

        $("#carouselExampleIndicators1").carousel('next');

    })

});

$(function(){
  // bind change event to select
  $('#exampleFormControlSelect2').change(function () {
          var url = $(this).val(); // get selected value
          if (url) { // require a URL
              window.location = url; // redirect
          }
          return false;
  });
});

