div.script:
$(document).ready(function () {
    $("#effaf4f8-3bdf-4442-9c36-227dbc338beb").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#f7e58522-8432-4115-a749-e4d793c9118b").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#f7e58522-8432-4115-a749-e4d793c9118b').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#f7e58522-8432-4115-a749-e4d793c9118b').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 6 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#819c1681-6881-4ed2-ba81-077e09e689dc').hide();
    jQuery('#819c1681-6881-4ed2-ba81-077e09e689dc').listSolrResults({
        currentPagePath: '/content/schools/mvca/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '819c1681-6881-4ed2-ba81-077e09e689dc',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

