function makeSimpleCalendarCompliant(){
  var prevButton = document.getElementsByClassName("simcal-prev");
  if (prevButton.length > 0){
    prevButton[0].innerHTML = "<";
  }
  var nextButton = document.getElementsByClassName("simcal-next");
  if (nextButton.length > 0){
    nextButton[0].innerHTML = ">";
  }
}
window.onload = makeSimpleCalendarCompliant;

