/*
Available options for the budget badge.
ALL OPTIONS ARE SHOWN AS DEFAULT
Any of the options can be deleted or omitted and the
default behavior will be exactly as if the options
were set like they are below.
*/
// Transparency URL.
var budgetBadgeTransparencyUrl = "/o/rcaps/browse/109875";
// Target for the Transparency link.
//  link target, such as "_blank", "_new", etc...
var budgetBadgeTransparencyTarget = '_self';
// Transparency Dashboard URL.
var budgetBadgeSchoolDataUrl = "http://bit.ly/2IBZll7";
// Target for the Transparency Dashboard link.
//  link target, such as "_blank", "_new", etc...
var budgetBadgeSchoolDataTarget = '_blank';
// Anchor element for the budget badge.
//  a CSS selector for the desired DOM element to anchor the budget badge to.
var budgetBadgeAnchor = '#header';
// Default Budget Badge Open
var defaultBudgetBadgeOpen = 'True';
// Budget Badge Position.
//  any value other than "left" will place the badge on the right of the page.
//  vertical placement is most efficiently done (and recommendded) through CSS changes.
var budgetBadgePosition = 'right';

      if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
      	$('body').removeClass('desktop');
      }
      $(document).ready(function() {

      	$(window).on('scroll', function() {
      		var delay = 0;
      		$('.main .col-item').children().each(function(i) {

      			var bottom_of_object = $(this).offset().top;
      			var bottom_of_window = $(window).scrollTop() + $(window).height();
      			if (bottom_of_window > bottom_of_object) {

      				var $li = $(this);
      				setTimeout(function() {
      					$li.addClass('active');
      				}, delay += 30);
      			}
      		});
      	});
      	$(window).trigger('scroll');


      	$('.slider-container').bxSlider({
      		mode: 'fade',
      		auto: true,
      		autoHover: true,
      		adaptiveHeight: true,
      		onSlideAfter: function() {
      			$('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');
      		}

      	});
      });

      var slider,
      	oBxSettings = {
      		auto: true,
      		autoHover: true,
      		adaptiveHeight: true,
      		slideWidth: 733,
      		slideMargin: 20,
			pager: false,
			nextText: 'Next News Headline',
			prevText: 'Previous News Headline'
      	};

      function init() {
      	oBxSettings.maxSlides = window.outerWidth < 800 ? 1 : 2;
      	oBxSettings.minSlides = window.outerWidth < 800 ? 1 : 2;
      	oBxSettings.slideMargin = window.outerWidth < 800 ? 0 : 20;
      	oBxSettings.mode = window.outerWidth < 800 ? 'fade' : 'horizontal';
      }

      $(document).ready(function() {
      	init();
      	slider = $('.news-slider').bxSlider(oBxSettings);
      });

      $(window).resize(function() {
      	if ((window.outerWidth < 800 && window.prevWidth >= 800) || (window.outerWidth >= 800 && window.prevWidth < 800)) {
      		init();
      		slider.reloadSlider(oBxSettings);
      	}
      	window.prevWidth = window.outerWidth;
      });
