 function AjaxFillRequestForm(){     $.ajax({url:"/include/srn_lib/psr/html_fragments/get_student_info.php",dataType:"json",cache:false,dataType:"json",success:function(c){
    if(c.logged_in===true){
        var user_type = $("input[name='user_type']");
        for(i=0;i<user_type.length;i++) {
            if(user_type[i].value == c.user_type){
                user_type[i].checked = "checked";
            }
        }
        $("input[name='email']").val(c.student_email);
        $("input[name='guardian_name']").val(c.guardian_name);
        $("input[name='first_name']").val(c.first_name);
        $("input[name='middle_name']").val(c.middle_name);
        $("input[name='last_name']").val(c.last_name);

        $("#birth_month").val(c.birth_month);
        $("#birth_date").val(c.birth_day);
        $("#birth_year").val(c.birth_year);
        $("#citizenship").val(c.citizenship);
        $("input[name='gender']").each(function(i){
            if(this.value == c.gender){ 
                this.checked='checked'
            }
        });
        if(c.current_grade=='0') {
            $("#current_grade").val("8");
        }else{
            $("#current_grade").val(c.current_grade);
        }
        if(c.seeking_grade=='0') {
            $("#seeking_grade").val("9");
        }else{
            $("#seeking_grade").val(c.seeking_grade);;
        }
        $("#seeking_season").val(c.seeking_season);
        $("#seeking_year").val(c.seeking_year);
        $("#school_name").val(c.school_name);
        $("input[name='school_type']").each(function(i){ 
            if(this.value == c.school_type){
                this.checked ="checked"
            }
        });
        $("input[name='financial_aid']").each(function(i){
            if(this.value == c.financial_aid){
                this.checked='checked'
            }
        });
        $("#description").val(c.description);
        $("#address").val(c.address);
        $("#city").val(c.city);
        $("#country").val(c.country);
        if(c.country != "United States of America") {
            $("#state").parent().hide().prev().hide();
        }else{
            $("#state").parent().show().prev().show();
        }
        $("#state").val(c.state);
        $("#zip").val(c.zip);
        $("#phone").val(c.phone);
        if(c.share_profile) {
            $("#share_profile").attr("checked",true);
        }else{
            $("#share_profile").attr("checked",false);
        }
        $(".error-icon").attr("class","valid-icon");
        $(".invalid-icon").attr("class","valid-icon");
        $("#share_profile").parent().next().hide();
        $("#save_profile").parents(".share_profile_table").hide();
    }else{
        $("input[name='user_type']").each(function(){this.checked=false});
        $("#user_type_s").attr("class","invalid-icon");

        $("#email").val('');
        $("#email_s").attr("class","invalid-icon");

        $("#guardian_name").val('');
        $("#guardian_name_s").attr('class','invalid-icon');

        $("#first_name").val('');
        $("#first_name_s").attr("class","invalid-icon");

        $("#middle_name").val('');
        $("#middle_name_s").attr("class","invalid-icon");

        $("#last_name").val('');
        $("#last_name_s").attr("class","invalid-icon");

