function RecommendFormSubmit(){
    $("#school_recommendation_form input[type=submit]").removeAttr("disabled");
    $("#school_recommendation_form").submit(function(){
        var description=$.trim($("#review_description").val());
        var name=$.trim($("#review_name").val());
        var review_by=$("#review_by").val();
        var score=$("#score").val();
        if(review_by=="0") {
            alert("Please select type (e.g., teacher, student, etc.)");
            $("#review_by").focus();
            return false;
        }
        if(name=='' || name=='Name or alias is required'){
            alert("Please enter name or alias");
            $("#review_name").focus();
            return false;
        }
        if(description==''){
            $("#review_description").val(description);
            alert("Recommendation is empty. Please enter school recommendation.");
            $("#review_description").focus();
            return false;
        }
        if(score==0){
            alert("Please select a score for your review");
            $("#score").focus();
            return false;
        }
        $(":submit", this).click(function() {
            return false;
        });
        hide_form();
        show_loading();
        //post
        $.ajax({
            type: "POST",
            url: "/include/srn_lib/psr/html_fragments/ajax_school_recommendation_web_service.php",
            data: $("#school_recommendation_form").serialize(), 
            success: function(data){
               success(data); 
            },
            error: function(jqXHR, textStatus, errorThrown) {  
               $('#error_message_container').html(jqXHR.responseText);
               error(jqXHR);
            }
        });
        return false;
    });
} 
