 function popup_resize(){
    if ($(window).width() < 700) {
        var width = $(window).width();
        $('.video_dialog_container').css('width', width);
        $('.youtube_video > iframe').css('width', width);

        var height = $(window).height();
        if (width > height) {
            height = height - $('#youtube_title').height() - 20;
        } else {
            height = Math.round(width/16)*9;
        }
        $('.youtube_video > iframe').css('height', height);
    }
}

function popup_video(e){
    e = e || window.event;
    e = $.event.fix(e);

    e.preventDefault();
    e.stopPropagation();

    var image;
    if ($(e.target).find('img').length) {
        image = $(e.target).find('img')[0];
    } else {
        image = $(e.target).parent().parent().find('img')[0];
    }

    $('#frame').attr('src', '//www.youtube.com/embed/'+image.src.split('/')[4]);
    $('#youtube_title').text(image.alt);
    $('#youtube_description').text(image.title);

    $('.video_dialog').show(0);

    var resizeInterval = setInterval(function(){
        if ($('.video_dialog_container').width() !== $(window).width()) {
            popup_resize();
        }
        if ($(window).width() > $(window).height()) {
            if ($('#youtube_title').height()+$('#frame').height()+ 20 !== $(window).height()) {
                popup_resize();
            }
        }
    }, 300);

    $('.close').click(function(){
        $('.video_dialog').hide(0);
        $('#frame').attr('src', '');
        clearInterval(resizeInterval);
    });
}

