        moveDom($("#infobox_top_right"),$("#ad-left-large-placeholder"),-mheight,0);
    }
    moveDom($("#request_information_box"),$("#request-placeholder"),0,0);
	moveDom($("#quick_stats"),$("#quick-stats-placeholder"),0,0);
}
