	function getYoutubeVideoCode(url)
	{
		var addressBarReg = /watch\?v=([^$&]+)/;
		var shareURLReg = /youtu\.be\/([^$&]+)/;

		var addressBarMatch = url.match(addressBarReg);
		var shareMatch = url.match(shareURLReg);

		return addressBarMatch.length > 1 ? addressBarMatch[1] : shareMatch[1];
	}

	function getYoutubeListCode(url)
	{
		var addressBarList = /watch\?list=([^$&]+)/;
		var shareURLReg = /youtu\.be\/([^$&]+)/;

    var addressBarMatchList = url.match(addressBarList);
		var shareMatch = url.match(shareURLReg);

		return addressBarMatchList.length > 1 ? addressBarMatchList[1] : shareMatch[1];
	}
