      e.preventDefault();

      var thisVid = $(this);

      if( thisVid.hasClass('yt') ){
          var youtubeURL = thisVid.find('span.ytURL').text();
          $('iframe#iframe').attr('src', 'https://www.youtube.com/embed/' + getYoutubeVideoCode(youtubeURL) );
      } else if( thisVid.hasClass('non-yt') ){
          var nonYoutubeURL = thisVid.find('span.non-ytURL').text();
          $('iframe#iframe').attr('src', nonYoutubeURL );
      }

    });

