span.script:
function ecmPopUpWindow(url, hWind, nWidth, nHeight, nScroll, nResize) {
 var popupwin,  cToolBar;
 if(nWidth == '9999' && nHeight == '9999'){
     cToolBar = 'toolbar=0,location=0,directories=0,status=' + nResize + ',menubar=0,scrollbars=' + nScroll + ',resizable=' + nResize + ',width=' + screen.width + ',height=' + screen.height;
     popupwin = window.open(url, hWind, cToolBar);
     popupwin.moveTo(0,0);
     popupwin.resizeTo(screen.availWidth, screen.availHeight); } else {
     cToolBar = 'toolbar=0,location=0,directories=0,status=' + nResize + ',menubar=0,scrollbars=' + nScroll + ',resizable=' + nResize + ',width=' + nWidth + ',height=' + nHeight;
     popupwin = window.open(url, hWind, cToolBar);
 } 
return popupwin;
}

div.script:
	$(document).ready(function() {
	
	    if ($('img[src*="btn_logout.gif"]').length == 0)
        {
	        $('div.userInfo').remove();
        }
	
	    var mainWidth = $('#loginWrapper').outerWidth();
    	
	    function show() {
		    var menu = $(this);
    		
		    $('#login').css('width', '20px').stop().animate({ 
			    width: mainWidth
		    }, 200 );
	    }
    	
	    function hide() { 
		    var menu = $(this);
    		
		    $('#login').stop().animate({ 
			    width: '20px'
		    }, 200 );
	    }
	    
	    //$('br')
    		  
	    $("#login").hoverIntent({
		    sensitivity: 1, // number = sensitivity threshold (must be 1 or higher)
		    interval: 0,   // number = milliseconds for onMouseOver polling interval
		    timeout: 0,   // number = milliseconds delay before onMouseOut
		    over: show,     // function = onMouseOver callback (required)
		    out: hide       // function = onMouseOut callback (required)
	    });
	    
	    $('span#ctl00_login1_login').children('br').remove();
	    
	});
	
	$(window).load(function(){   
		//$('#loading').css('visibility', 'hidden');
		$('#loginIcon').css('background-image', 'url(/images/loginBtn.png)');
	});

    $(document).ready(function () {
        $('#searchtext').on('keydown', function(e) {
            if (e.keyCode === 13) {
                window.location.href = '/search.aspx?q=' + $('#searchtext').val();
                e.preventDefault();
            }
        });

        $('#searchbtn').click(function() {
            window.location.href = '/search.aspx?q=' + $('#searchtext').val();
        });

        // focus search text on search link click
        $('#searchlink').click(function() {
            setTimeout(function() {
                $('#searchtext').focus();
            }, 200);
        });
    });

