     jQuery(function (q) {
         q(".form_div_870").validationEngine({
             promptPosition: "topRight"
         });

         q(".form_div_870 input[lang='Submit']").click(function () {
             if (!$('.form_div_870').validationEngine('validate')) {
                 return false;
             }
         });

     });

     function CancelValidation() {
         jQuery('#Form').validationEngine('detach');
     }

