body.script:
$.fn.dataTableExt.oSort['nullable-asc'] = function (a, b) {
            if (!a)
                return 1;
            else if (!b)
                return -1;
            else {
                var fa = parseFloat(a);
                var fb = parseFloat(b);
                return (fa < fb) ? -1 : ((fa > fb) ? 1 : 0);
            }
        }

        $.fn.dataTableExt.oSort['nullable-desc'] = function (a, b) {
            if (!a)
                return 1;
            else if (!b)
                return -1;
            else {
                var fa = parseFloat(a);
                var fb = parseFloat(b);
                return (fa > fb) ? -1 : ((fa < fb) ? 1 : 0);
            }
        }

        $(document).ready(function () {
            $('.tableAuto').DataTable({
                dom: '<flp>',
                responsive: {
                    details: false
                },
                columnDefs: [{
                    "targets": 'nullSort',
                    "bSortable": true, "sType": "nullable"
                }],
                "fnDrawCallback": function (oSettings) {
                    this.show();
                    this.api().columns.adjust();
                },
                "language": {
                    "processing": "",
                    "info": "Showing _START_ to _END_ of _TOTAL_",
                    "infoEmpty": "Sorry, there is no match for your search",
                    "infoFiltered": "(filtered from _MAX_ rows)",
                    "emptyTable": "Sorry, there is no match for your search criteria.",
                    "search": "Find:",
                    "lengthMenu": "Show _MENU_ at a time"
                },
                preDrawCallback: function (settings) {
                    var api = new $.fn.dataTable.Api(settings);
                    var pagination = $(this)
                        .closest('.dataTables_wrapper')
                        .find('.dataTables_paginate');

                    if (api.page.info().pages <= 1) {
                        pagination.hide();
                    }
                    else {
                        pagination.show();
                    }
                },
                autoWidth: false
            });
        });

