$(document).ready(function () {
    $('#chkRepopulateMap').change(function () {
        if ($(this).is(":checked")) {
            bRepopulateOnMapMove = true;
            bBeenHereOnce = true;
            $('#gmLoadMoreSchools').val('true')
        }
        else {
            bRepopulateOnMapMove = false;
            $('#gmLoadMoreSchools').val('false')
        };
    });
    $('#btnMapBig').hide();    
});

