//Capture window resize http://stackoverflow.com/questions/18575582/how-to-detect-responsive-breakpoints-of-twitter-bootstrap-3-using-javascript
$(window).resize(function () {
    waitForFinalEvent(function () {
        if ($(document).width() < 500) {
            $('.btnFIPS').hide();
            //Go to US
            sd2ACChangeFIPS('', 'U.S.')
        }
        else {
            $('.btnFIPS').show();
        }
    }, 300, fullDateString.getTime())
});
$(document).ready(function () {
    $('.txtACOmni').keypress(function (e) {
        if (e.which == 13) {
            acSubmit($(e.currentTarget).closest("div.input-group"));
            return (false);
        }
    });
    $('.btnAC').click(
        function (e) {
            acSubmit($(e.currentTarget).closest("div.input-group"));
            return (false);
        });
});

