    Highcharts.visualizecsc2122931263 = function(table, options) {
        // the categories
        options.xAxis.categories = [];
        //
        $('tbody tr td:first-child', table).each( function(i) {
            //We should have a link and a school name. If name > 20 chars, truncate
            var sName = $(this).text();
            var sNameandURL = this.textContent.replace('&','&');
            if (sName.length > 20) {
                var sNewName = sName.substr(0,18) + '...';
                sNameandURL = sNameandURL.replace(sName, sNewName)
            }
            options.xAxis.categories.push(sNameandURL);
        });
   
        // the data series
        options.series = [];
        var offset = -1;
        $('tr', table).each( function(i) {
            var tr = this;
            $('th, td', tr).each( function(j) {
                if (j > 0) { // skip first column

                    if (i == 0) { // get the name and init the series
                        //Ignore TOTAL column
                        if (!(this.textContent.indexOf('Total') >= 0) && this.textContent.toLowerCase() != 'schooldigger rank' && this.textContent.toLowerCase() != 'dist (miles)' && this.textContent.toLowerCase() != 'zip code') {
                            if (offset == -1){ offset = j}
                            options.series[j - offset] = {
                                name: this.textContent,
                                data: []
                            }
                        };
                    } else { // add values
                        //Make sure this is a numeric value, otherwise zero
                        if (options.series[j - offset] != null) {
                            //Get x-axis url A, then parse out the URL value
                            var sURL = getURLFromA(options.xAxis.categories[i-1]);
                            if (isNumeric(this.textContent.replace(' %',''))) { options.series[j - offset].data.push({y:parseFloat(this.textContent),url:sURL});}
                            else
                                //Check for no data -- if so add a null.  Otherwise, 0
                            { if(this.textContent == '(no data)') {options.series[j - offset].data.push(null);} else{options.series[j - offset].data.push(0);}}              }
                    }
                }
            });
        });
                  
        var c2122931263 = new Highcharts.Chart(options);
    }

