Highcharts.visualizec1382222478 = function(table, options) {
    // the categories
    options.xAxis.categories = [];
    $('tbody tr td:first-child', table).each( function(i) {
        options.xAxis.categories.push(this.textContent);
    });
   
    // the data series
    options.series = [];
    $('tr', table).each( function(i) {
  
        var tr = this;
        $('th, td', tr).each( function(j) {
            if (j == 4) { // only get one column

                if (i == 0) { // get the name and init the series
                    options.series[0] = { 
                        name: this.textContent,
                        data: []
                    };
                } else { // add values
                    //Make sure this is a numeric value, otherwise zero
                    if (options.series[0] != null) {
                        //Check for %
                        var numCheck = this.textContent.replace('%','');
                        if (isNumeric(numCheck)) { options.series[0].data.push(parseFloat(this.textContent));}
                        else
                            //Check for no data -- if so add a null.  Otherwise, 0
                        { if(this.textContent == 'n/a') {options.series[0].data.push(null);} else{options.series[0].data.push(0);}}
                    }
                }
            }
        });
    });
   
    var c1382222478 = new Highcharts.Chart(options);
}

Highcharts.visualizec1382222478(table, options);
});
    
});

