$("#txtEmail").keypress(function (event) {
    var keycode = (event.keyCode ? event.keyCode : event.which);
    if (keycode == '13') {
        event.preventDefault();
        event.stopPropagation();
        $('#btnSubmitEmail').click();
    }
});
$('#btnSubmitEmail').click(function () {
    var em = $("#txtEmail")[0].value;
    sendUpdateEmail('#lblEmailError', scFips, scLEAID, scSCHOOLNUM, em, 2);
});

$('#zBtnRent').bind('click', function (event, ui) {
    $('#zBtnRent').addClass("btn-primary").removeClass("btn-default");
    $('#zBtnSale').addClass("btn-default").removeClass("btn-primary");
    zListType = 1;
    zLoad();
});
$('#zBtnSale').bind('click', function (event, ui) {
    $('#zBtnSale').addClass("btn-primary").removeClass("btn-default");
    $('#zBtnRent').addClass("btn-default").removeClass("btn-primary");
    zListType = 0;
    zLoad();
});

var _sf_async_config = { uid: 29659, domain: 'schooldigger.com', useCanonical: true };
(function () {
    function loadChartbeat() {
        window._sf_endpt = (new Date()).getTime();
        var e = document.createElement('script');
        e.setAttribute('language', 'javascript');
        e.setAttribute('type', 'text/javascript');
        e.setAttribute('src', '//static.chartbeat.com/js/chartbeat.js');
        document.body.appendChild(e);
    };
    addLoadEvent(loadChartbeat);
})();

(function (w, i, d, g, e, t, s) {
    w[d] = w[d] || []; t = i.createElement(g);
    t.async = 1; t.src = e; s = i.getElementsByTagName(g)[0]; s.parentNode.insertBefore(t, s);
})(window, document, '_gscq', 'script', '//widgets.getsitecontrol.com/50023/script.js');
_gscq.push(['targeting', 'show', '1']);

$(document).on('keydown', function (e) {
    if ((e.metaKey || e.altKey) && (String.fromCharCode(e.which).toLowerCase() === '1')) {
        $('.diagnostics').toggle();
    }
});
