    $(".search_icon_inp_btn").click(function() {
        hideInfobox();
      //   alert($('.search_box_container').width());
         left_margin = $('.search_box_container').width() + 22;

        if(click==="1")
        {
          //  $('.search_box').animate({"left":"-38%"});
            $('.search_box').animate({"left":(left_margin*-1)+"px"});
            $('.search_box_container').animate({"opacity":"0"});
            $('.search_icon_inp_btn').html('');
            click="0";
        }
        else
        {
            $('.search_box_container').animate({"opacity":"1"});
            $('.search_box').animate({"left":"0px"});
            $('.search_icon_inp_btn').html('');
            click="1";
        }

