            // Create and populate the data table.
    var data = google.visualization.arrayToDataTable([
        ['Subject', 'School 1', 'School 2', 'School 3', 'State Avg'],
        ['',   null,    null, null,      100],
        ['Overall Math',  16,      93,   20,    100],
        ['Overall Reading',  13,      110,   35,    100],
        ['Overall Science',  15,      11,   65,    100],
        ['School Average',  19,      80,   25,    100],
        ['',   null,    null,   null,    100]
    ]);

    var ticks = [];
    // ignore the first and last rows
    for (var i = 1; i < data.getNumberOfRows() - 1; i++) {
        ticks.push({v: i, f: data.getValue(i, 0)});
    }

    var view = new google.visualization.DataView(data);
    view.setColumns([{
        type: 'number',
        label: data.getColumnLabel(0),
        calc: function (dt, row) {
            return {v: row, f: dt.getValue(row, 0)};
        }
    }, 1, 2, 3,4]);

    var range = view.getColumnRange(0);
    var offset = 0.5; // change this value to adjust the padding to the left and right of the columns in the chart

    // Create and draw the visualization.
    var ac = new google.visualization.ComboChart(document.getElementById('graph_container'));
    var formatter = new google.visualization.NumberFormat({
        suffix: '%'
    });
    formatter.format(data, 1);
    formatter.format(data, 2);
    formatter.format(data, 3);

    ac.draw(view, {
        height: '240',
        width:'70%',
        chartArea: {
            width: '90%',
            height: '80%'
        },
        colors:['#004D8D','#FF9132','#F7EC56'],
        legend:{position:'none'},
        chartArea:{top:25,width:"80%",height:"75%"},
        vAxis: {
            title: "",
            format: '#\'%\'',
            ticks: [{v:0, f:'0'},{v:50, f:'50%'},{v:100, f:'State Avg'},{v:150, f:'150%'}]
        },
        hAxis: {
            title: "",
            ticks: ticks
            ,
            viewWindow: {
                min: range.min + offset,
                max: range.max - offset
            },
            gridlines: {
                // hide vertical gridlines to match discrete chart display
                color: 'transparent'
            }
        },
        seriesType: "bars",
        series: {
            3: {
                type: "line",
                visibleInLegend: false,
                color: "red"
            }
        }
    });
      }

