        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429072909501';
        var calMonthFullId = 'miniCalMonthFull20200429072909501';
        var calMonthShortId = 'miniCalMonthShort20200429072909501';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429072909501';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429072909501';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

