$(".group871052").colorbox({rel:'group871052', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group871052").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery871052 = $("a[rel=group871052]").colorbox({maxWidth: "80%"});
$("a#openGallery871052").click(function(e){
    e.preventDefault();
    $gallery871052.eq(0).click();
});

