$(document).ready(function() {
	var colAdjust = function() {
		var $card1 = $('div#news-1 div.card');
		var $img1 = $card1.find('img.card-img-top');
		var $body1 = $card1.find('div.card-body');
		var $footer1 = $card1.find('div.card-footer');
		var $card2 = $('div#news-2 div.card');
		var $img2 = $card2.find('img.card-img-top');
		var $body2 = $card2.find('div.card-body');
		var $footer2 = $card2.find('div.card-footer');
		var $card3 = $('div#news-3 div.card');
		var $img3 = $card3.find('img.card-img-top');
		var $body3 = $card3.find('div.card-body');
		var $footer3 = $card3.find('div.card-footer');
		var h1 = Math.ceil($img1.height()+$body1.height()+$footer1.height()) + 40;
		var h2 = Math.ceil($img2.height()+$body2.height()+$footer2.height()) + 40;
		var h3 = Math.ceil($img3.height()+$body3.height()+$footer3.height()) + 40;
		if ((h1 == h2) && (h2 == h3)) {
			//Nothing to do?
		} else if ((h1 >= h2) && (h1 >= h3)) {
			$card1.height(h1);
			$card2.height(h1);
			$card3.height(h1);
		} else if ((h2 >= h1) && (h2 >= h3)) {
			$card1.height(h2);
			$card2.height(h2);
			$card3.height(h2);
		} else {
			$card1.height(h3);
			$card2.height(h3);
			$card3.height(h3);
		}
	}
	repeatXI(colAdjust, 500, 10, true);
	$(window).resize(colAdjust);
});

function repeatXI(callback, interval, repeats, immediate) {
	var timer, trigger;
  	trigger = function () {
		callback();
		--repeats || clearInterval(timer);
  	};
  	interval = interval <= 0 ? 1000 : interval;
  	repeats  = parseInt(repeats, 10) || 0;      
  	timer    = setInterval(trigger, interval);
  if( !!immediate ) {
	trigger();
  }
}
