div.script:
jQuery(function ($) {
	var jcarousel = $('.jcarousel');
	jcarousel.jcarousel({
		wrap: 'circular'
	});

	$('.jcarousel-control-prev')
		.jcarouselControl({
			target: '-=1'
		});

	$('.jcarousel-control-next')
		.jcarouselControl({
			target: '+=1'
		});
});

jQuery(function ($) {
	$('#accomplishments-2 .cycle-slideshow').each(function () {
		var slider = $(this);
		var $_args = {
			cleartypeNoBg: true,
			fit: 1,
			next: slider.find('.cycle-next'),
			pager: slider.find('.cycle-pager'),
			pagerEvent: 'click',
			pause: 1,
			prev: slider.find('.cycle-prev'),
			slideExpr: '.slide',
			speed: 500,
			timeout: 0
		};
		slider.cycle($_args);

		slider.touchwipe({
			wipeLeft: function () {
				slider.cycle('next');
			},
			wipeRight: function () {
				slider.cycle('prev');
			},
			preventDefaultEvents: false
		});
	});
});

body.script:
      jQuery(document).ready(function($) {
        $("a.nturl").on("click",function() {
	  default_lang = "en";
	  lang_prefix = $(this).attr("class").split(" ")[2];
			  
	  if (lang_prefix == default_lang) {			   
	    load_default();
	  } else {
	    load_selected_language();
	  }
			 
	  function load_default() {
	    doGoogleLanguageTranslator(default_lang + "|" + default_lang);
	  }
			 
	  function load_selected_language() {
	    doGoogleLanguageTranslator(default_lang + "|" + lang_prefix);
	  }
        });
		  
        $("a.flag").on("click",function() {
          default_lang = "en";
	  lang_prefix = $(this).attr("class").split(" ")[2];
			  
