			if(event.source.ajaxSettings.calName && typeof event.title == "string")
			{
				// console.log("has a calname attr");
				if(!event.title.startsWith(event.source.ajaxSettings.calName))
				{
					// console.log("missing calname at start");
					newTitle = event.source.ajaxSettings.calName + " - " + event.title;

					var title = element.find('.fc-title, .fc-list-item-title');          
					title.html(newTitle);
					// title.innerHTML = title.innerHTML.replace(event.title,newTitle);

					// element[0].innerHTML = element[0].innerHTML.replace(event.title,newTitle);
				}
			}
		},
		height: 700
	});
});

$(window).on('load', function() {
	var div = $('.fc-scroller');

	var now = new Date();
	var nextSunday = nextSession(now);

	// loop through dates
	for (var d = now; d <= nextSunday; d.setDate(d.getDate() +1)) {
		// curDate = d.getFullYear() + "-" + (d.getMonth()+1) + "-" + d.getDate();
		curDate = ('0' + d.getFullYear()).slice(-4) + '-'
			 + ('0' + (d.getMonth()+1)).slice(-2) + '-'
			 + ('0' + (d.getDate())).slice(-2);
		trsel = 'tr[data-date="' + curDate + '"]';

		vPos = $(trsel).position();

		if( vPos ) {
			// console.log(vPos.top);
			div.scrollTop(vPos.top);
			break;
		}
	}
});



});
function nextSession(date) {
	var ret = new Date(date||new Date());
	ret.setDate(ret.getDate() + (7 - 1 - ret.getDay() + 7) % 7 + 1);
	return ret;
}

