link.script:
    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": false,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
		"ShowFullSiteButton": false,
        "ChannelBarIsSticky": false,
        "ScrollLock": 0,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.DynamicStyleSheet();
			this.MyStart();
			this.SchoolList();
			this.Translate();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Slideshow();
            this.Body();
            this.ModEvents();
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.JsMediaQueries();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			csGlobalJs.OpenInNewWindowWarning();

            $(".bullet").each(function() {
            	$(this).insertAfter($(this).siblings("a"));
            });

            if($('.hp[data-gallery-type="Large Photo Gallery"] #hp-slideshow-outer .app.gallery.json').length || $('.hp[data-gallery-type="Large Photo Gallery"] #hp-slideshow-outer .app.multimedia-gallery').length) {
            	this.StickySlideshow();
            }
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyChannelBar();

            if($('#hp-slideshow-outer .app.gallery.json').length || $('#hp-slideshow-outer .app.multimedia-gallery').length) {
            	this.FixedHeightSlideshow();
            }

            if($('.hp[data-gallery-type="Large Photo Gallery"] #hp-slideshow-outer .app.gallery.json').length || $('.hp[data-gallery-type="Large Photo Gallery"] #hp-slideshow-outer .app.multimedia-gallery').length) {
            	this.StickySlideshow();
            }
        },

        "WindowScroll": function() {
            this.StickyChannelBar();

            if($('#hp-slideshow-outer .app.gallery.json').length || $('#hp-slideshow-outer .app.multimedia-gallery').length) {
            	this.FixedHeightSlideshow();
            }

            if($('.hp[data-gallery-type="Large Photo Gallery"] #hp-slideshow-outer .app.gallery.json').length || $('.hp[data-gallery-type="Large Photo Gallery"] #hp-slideshow-outer .app.multimedia-gallery').length) {
            	this.StickySlideshow();
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
					$("#hp-slideshow .rs-photo-gallery-more-link, #hp-slideshow .rs-photo-gallery-controls").appendTo($("#hp-slideshow .rs-photo-gallery-container"));
                break;
                case "768":
					$("#hp-slideshow .rs-photo-gallery-controls").appendTo($("#hp-slideshow .rs-photo-gallery-images"));
                    $("#hp-slideshow .rs-photo-gallery-more-link").appendTo($("#hp-slideshow .rs-photo-gallery-desc"));
                break;
                case "640":
					$("#hp-slideshow .rs-photo-gallery-controls").appendTo($("#hp-slideshow .rs-photo-gallery-images"));
                    $("#hp-slideshow .rs-photo-gallery-more-link").appendTo($("#hp-slideshow .rs-photo-gallery-desc"));
                break;
                case "480":
					$("#hp-slideshow .rs-photo-gallery-controls").appendTo($("#hp-slideshow .rs-photo-gallery-images"));
                    $("#hp-slideshow .rs-photo-gallery-more-link").appendTo($("#hp-slideshow .rs-photo-gallery-desc"));
                break;
                case "320":
					$("#hp-slideshow .rs-photo-gallery-controls").appendTo($("#hp-slideshow .rs-photo-gallery-images"));
                    $("#hp-slideshow .rs-photo-gallery-more-link").appendTo($("#hp-slideshow .rs-photo-gallery-desc"));
                break;
            }
        },

        "DynamicStyleSheet": function() {
        	//Variables
            var regionDEBGSrc = $.trim('/cms/lib/CA01000408/Centricity/Template/GlobalAssets/images///accent_images/IMG_4908.jpg');
            var srcCheck = regionDEBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var styles = "";

            if((regionDEBGSrc != "") && (!srcCheck)) {
                styles += '.hp-row.two:before { \n'+   
                              "\t background: url('"+regionDEBGSrc+"') no-repeat fixed; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '.hp-row.two:before { \n'+   
                              "\t background: url('/cms/lib/CA01000408/Centricity/Template/10/defaults/hp-content-background.jpg') no-repeat fixed;\n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            }

            styles += "#gb-header .drt-discover-icon:hover, #gb-header .drt-discover-icon:hover:focus {\n"+
                          "\t color: rgba("+this.HexToRgb('#18471A')+", 0.5);\n"+
                      "}\n"+
                      ".sw-mystart-button.home a:hover, .cs-mystart-dropdown.schools:hover .cs-selector, .sw-mystart-button.home a:focus, .cs-mystart-dropdown.schools:focus .cs-selector {\n"+     
