                      "}\n"+
                      "#hp-slideshow .mmg-description-link {\n"+     
                          "\t background: rgba("+this.HexToRgb('#18471A')+", 1);\n"+
                      "}\n"+
                      "#hp-slideshow .mmg-description-link:hover, #hp-slideshow .rs-photo-gallery-more-link:hover, #hp-slideshow .rs-photo-gallery-desc-more-link:hover, #hp-slideshow .rs-photo-gallery-more-link:focus, #hp-slideshow .rs-photo-gallery-desc-more-link:focus {\n"+     
                          "\t background: rgba("+this.HexToRgb('#18471A')+", 0.5);\n"+
                      "}\n"+
                      ".view-calendar-link:hover, view-calendar-link:focus,\n"+
                      "\tdiv.ui-widget.app .more-link:hover, div.ui-widget.app .more-link:focus,\n"+
                      "\tdiv.app-level-social-rss a.ui-btn-toolbar.rss:hover, div.app-level-social-rss a.ui-btn-toolbar.rss:focus,\n"+
                      "\ta.ui-btn-toolbar.calendarfollow:hover, a.ui-btn-toolbar.calendarfollow:focus {\n"+     
                          "\t background: rgba("+this.HexToRgb('#2C8150')+", 0.5);\n"+
                      "}"+
                      ".gb-footer-button:hover, .gb-footer-button:focus {\n"+     
                          "\t background: rgba("+this.HexToRgb('#2C8150')+", 0.5);\n"+
                      "}";

            $('head').append(
                $('', {
                    id: 'cs-dynamic-background-styles',
                    html: styles
                })
            );
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

			// ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home > a > span").text("District Home");
            	$(".gb-header.right").append($(".sw-mystart-button.home"));
            }

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

            // BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

            $("div:not(.schools) .cs-selector-text").each(function() {
            	$(this).width($(this).width()+76);
            });

            $("div:not(.schools) .cs-selector, #ui-btn-mypasskey").hover(function() {
            	$(".cs-selector-text", this).fadeIn(250);
            }, function() {
            	$(".cs-selector-text", this).fadeOut(250);
            });

            $(".cs-mystart-dropdown:not(.schools) .cs-selector, #ui-btn-mypasskey").focus(function() {
            	$(".cs-selector-text", this).fadeIn(250);
            }).blur(function() {
            	$(".cs-selector-text", this).fadeOut(250);            
            });
        },

