        "StickySlideshow": function() {
            var chanelHeight = $("#gb-channel-list-outer").outerHeight(true);
            var headerHeight = $("#gb-header-outer").outerHeight(true);
            var slideshowHeight = chanelHeight + headerHeight + $("#hp-slideshow-outer").outerHeight(true);
            if(slideshowHeight < $(window).height()){
            	$("#hp-slideshow .rs-photo-gallery-controls").addClass("unfixed");
                $("#hp-slideshow .rs-photo-gallery-desc, #hp-slideshow .mmg-description-outer").addClass("unfixed");
            }

            if(!$(".rs-photo-gallery-controls").length) {
            	if($("#hp-slideshow .mmg-description-outer").length) {
            		$("#hp-slideshow .mmg-slides-outer").append('
');
                } else if($("#hp-slideshow .rs-photo-gallery-desc").length) {
                	$("#hp-slideshow .rs-photo-gallery-container").append('
');
                }
            }
            var controlsTop = $("#hp-slideshow .rs-photo-gallery-controls").offset().top;
            var controlsBottom = controlsTop + 90;
            var slideshowTop = $("#hp-slideshow-outer").offset().top;
       		var slideshowBottom = slideshowTop + $("#hp-slideshow-outer").outerHeight(true);
            var scrollTop = $(window).scrollTop();
            var scrollBottom = scrollTop + $(window).height();

            if(scrollBottom < slideshowBottom) {
                if(controlsBottom <= slideshowBottom) {
                    $(".rs-photo-gallery-controls").removeClass("unfixed");
                    $(".rs-photo-gallery-desc, #hp-slideshow .mmg-description-outer").removeClass("unfixed");
                } else {
                    $("#hp-slideshow .rs-photo-gallery-controls").addClass("unfixed");
                    $("#hp-slideshow .rs-photo-gallery-desc, #hp-slideshow .mmg-description-outer").addClass("unfixed");
                }
            } else {
            	$("#hp-slideshow .rs-photo-gallery-controls").addClass("unfixed");
                $("#hp-slideshow .rs-photo-gallery-desc, #hp-slideshow .mmg-description-outer").addClass("unfixed");
            }
        },

        "FixedHeightSlideshow": function() {
            // FOR SCOPE
            var _this = this;

            var scrolled = $(window).scrollTop(); 
            if($("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
            	var loc = $('#hp-slideshow-outer .app.gallery.json').offset().top+$('#hp-slideshow-outer .app.gallery.json').outerHeight();
            } else {
            	var loc = $('#hp-slideshow-outer .app.multimedia-gallery').offset().top+$('#hp-slideshow-outer .app.multimedia-gallery').outerHeight();
            }

            if(scrolled >= loc && _this.ScrollLock === 0) {
            	if($("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                	$("#hp-slideshow-outer .app.gallery.json .rs-photo-gallery-desc").height($("#hp-slideshow-outer .app.gallery.json .rs-photo-gallery-desc").height());
                } else {
                	$("#hp-slideshow-outer .app.multimedia-gallery .mmg-description-outer").height($("#hp-slideshow-outer .app.multimedia-gallery .mmg-description-outer").height());
                }
                _this.ScrollLock = 1;
            } else if(scrolled < loc && _this.ScrollLock == 1){
            	if($("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                	$("#hp-slideshow-outer .app.gallery.json .rs-photo-gallery-desc").height("auto");
                } else {
                	$("#hp-slideshow-outer .app.multimedia-gallery .mmg-description-outer").height("auto");
                }
                _this.ScrollLock = 0;
            }
        },

        "ModEvents": function() {
            var months = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
			var monthsLong = ["January","February","March","April","May","June","July","August","September","October","November","December"];
            var loopDay = "";

            $(".ui-widget.app.upcomingevents").modEvents({
				columns     : "yes",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {                
				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length){
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".upcoming-column.right").append(moveArticle);
					$(this).parent().remove();
				}

                if($.trim($(".ui-article-description", this).text()) != "There are no upcoming events to display.") {
                      if($(".joel-day", this).length && $(".joel-day", this).text() != loopDay) {
                          loopDay = $(".joel-day", this).text();
                      }

                      var ariaMonth = monthsLong[monthsLong.indexOf($(".joel-month", this).text())];
                      var eventMonth = $(".joel-month", this);
                      var eventDay = $(".joel-day", this);
                      $(".joel-day, .joel-month", this).remove();
                      var eventDate = $.trim($(".ui-article-title", this).text());
                      if(eventDate.length == 1) eventDate = "0" + eventDate;

                      var ariaDate = eventDate;
                      switch(eventDate) {
                          case "01": ariaDate = "First"; break;
                          case "02": ariaDate = "Second"; break;
                          case "03": ariaDate = "Third"; break;
                          case "04": ariaDate = "Fourth"; break;
                          case "05": ariaDate = "Fifth"; break;
                          case "06": ariaDate = "Sixth"; break;
                          case "07": ariaDate = "Seventh"; break;
                          case "08": ariaDate = "Eighth"; break;
                          case "09": ariaDate = "Ninth"; break;
                          case "10": ariaDate = "Tenth"; break;
                          case "11": ariaDate = "Eleventh"; break;
                          case "12": ariaDate = "Twelveth"; break;
                          case "13": ariaDate = "Thirteenth"; break;
                          case "14": ariaDate = "Fourteenth"; break;
                          case "15": ariaDate = "Fifteenth"; break;
                          case "16": ariaDate = "Sixteenth"; break;
                          case "17": ariaDate = "Seventeenth"; break;
                          case "18": ariaDate = "Eighteenth"; break;
                          case "19": ariaDate = "Nineteenth"; break;
                          case "20": ariaDate = "Twentieth"; break;
                          case "21": ariaDate = "Twenty-first"; break;
                          case "22": ariaDate = "Twenty-second"; break;
                          case "23": ariaDate = "Twenty-third"; break;
                          case "24": ariaDate = "Twenty-fourth"; break;
                          case "25": ariaDate = "Twenty-fifth"; break;
                          case "26": ariaDate = "Twenty-sixth";  break;
                          case "27": ariaDate = "Twenty-seventh"; break;
                          case "28": ariaDate = "Twenty-eighth"; break;
                          case "29": ariaDate = "Twenty-ninth"; break;
                          case "30": ariaDate = "Thirtieth"; break;
                          case "31": ariaDate = "Thirty-first"; break;
                      }

                      $(".ui-article-title", this).attr("aria-label", $(eventDay).text() + ", " + ariaMonth + " " + ariaDate).text(eventDate).prepend(''+$(eventDay).html()+''+$(eventMonth).html()+'');
                }

                $(".joel-day, .joel-month", this).wrapAll('
');
                $(".joel-day", this).text($(".joel-day", this).text().substring(0, 3));
                $(".joel-month", this).text($(".joel-month", this).text().substring(0, 3));
			});

            $(".upcomingevents .ui-article-description").each(function() {
            	$(".sw-calendar-block-time", this).appendTo($(this));
            });

            $('.upcomingevents').each(function() {
               if(!$('.ui-article', this).length) {
                   $('.ui-articles',this).html('




There are no upcoming events to display.');
               }
            });
        },

