        "Search": function() {
            // FOR SCOPE
            var _this = this;

            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
            	if($("#gb-search-input").val() != "" && $("#gb-search-input").val() != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });

            //Show/hide search
            $(document).on("click keydown", ".cs-mystart-item.search", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search").attr("aria-hidden", "false").slideDown(250);

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search").attr("aria-hidden", "true").slideUp(250);
                    }
                }
            });

            $(document).on("keydown", "#gb-search-input", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                        e.preventDefault();
                        $("#gb-search-button").focus();
                    break;
                }
            });

            $(document).on("keydown", "#gb-search-button", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                        e.preventDefault();
                        $(".cs-mystart-item.search").click().focus();
                    break;
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        }
    };

