        "DropdownActions": function(params) {
            // FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                    $("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                    $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                            $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey) {
                            e.preventDefault();

                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case template.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;
                }
            });

            var dropdownTimer;
            $(dropdownParent).mouseenter(function() {
            	clearTimeout(dropdownTimer);
            }).mouseleave(function() {
            	var thisDropdownParent = this;

				dropdownTimer = setTimeout(function(){
                	if($(thisDropdownParent).find("#google_translate_element").length) {
                    	if(!$(thisDropdownParent).find("#cs-branded-translate-label").is(":focus")){
                        	$(dropdownList + " a", thisDropdownParent).attr("tabindex", "-1");
                            $(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        } else {
                        	$("#cs-branded-translate-label").on("change", function() {
								$(".goog-logo-link").focus();
                            });
                        }
                    } else {
                		$(dropdownList + " a", thisDropdownParent).attr("tabindex", "-1");
						$(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            		}
                }, 500);
            }).focusout(function() {
                var thisDropdownParent = this;

                setTimeout(function () {
                    if(!$(thisDropdownParent).find(":focus").length && !$(thisDropdownParent).find("#google_translate_element").length) {
                        $(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/CA50000164/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var srcSplit = logoSrc.split("/");
            var srcSplitLen = srcSplit.length;
            if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                $("header .gb-logo").append("").removeClass("hidden");
            } else {
                $("header .gb-logo").append("").removeClass("hidden");
            }

            //REMOVE SITENAME & TAGLINE IF EMPTY OR NOT IN USE
            if($(".sitename").text().trim() == "" || $("header").attr("data-logo-only") === "true"){
                $(".sitename").remove();
            }

            if($(".site-tagline").text().trim() == "" || $("header").attr("data-logo-only") === "true"){
                $(".site-tagline").remove();
            }  

            //CHECK FIND IT FAST LINKS
            var fifLinks = [true, true, true, true, true, true, true, true];

        	for(i=0; i < fifLinks.length; i++){
            	if(fifLinks[i] == true){
                	$(".cs-mystart-dropdown.find-it-fast").show();
                }
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //MOVE HOMEPAGE AND CALENDAR LINKS
            if($("#navc-HP").length){
                var homeLink = $("#navc-HP a").attr("href");
                $(".channel-icon-link.home").attr("href", homeLink).show();
                $("#navc-HP").remove();
            }
            if($("#navc-CA").length){
                var calLink = $("#navc-CA a").attr("href");
                $(".channel-icon-link.calendar").attr("href", calLink).show();
                $("#navc-CA").remove();
            }
        },

        "Body": function() {
            // FOR SCOPE
            var _this = this;

            //SCROLL DOWN
            $("#scroll-down").on('click keypress', function(e){
                    if(_this.AllyClick(e) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                        $('html, body').animate({
                            scrollTop: $(this).offset().top + 150
                    }, 500);
                }
            });

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

            // CHECK PAGELIST HEADER
            if($.trim($(".ui-widget.app.pagenavigation .ui-widget-header").text()) == "") {
                $(".ui-widget.app.pagenavigation .ui-widget-header").html("
");
            }

            //CHANGE RSS TEXT
            $(".app-level-social-rss a").html("Subscribe");  

            //REMOVE UNUSED LINKS
            $("[data-toggle='false']").remove();

        },

        "Homepage": function() {
            // FOR SCOPE
            var _this = this;

            if($("#gb-page.hp").length){

                //HEADLINES SPOTLIGHT
                if($(".hp-row.four .headlines").length > 1){
                    $(".hp-row.four .headlines").eq(0).addClass("spotlight");
                    $(".hp-row.four").addClass("spotlight");

                    //HEADLINES SLIDER - SPOTLIGHT
                    $(".hp-row.four .ui-widget.app.headlines:not(.spotlight)").csArticleSlider({
                        "viewerNum": [3, 2, 2, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                        "transitionSpeed": .5, // SECONDS
                        "slideFullView": false,
                        "extend": function(config, element, ArticleSlider) {}
                    });
                } else {
                    //HEADLINES SLIDER - NO SPOTLIGHT
                    $(".hp-row.four .ui-widget.app.headlines:not(.spotlight)").csArticleSlider({
                        "viewerNum": [4, 2, 2, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                        "transitionSpeed": .5, // SECONDS
                        "slideFullView": false,
                        "extend": function(config, element, ArticleSlider) {}
                    });
                }
                $(".hp-row.four .headlines").show();

                //HOVERS
                $(".hp-row.three .upcomingevents li").hover(function() {
                    $(this).addClass("hover");
                }, function() {
                    $(this).removeClass("hover");
                });

                //FOCUS
                $(".hp-row.three .upcomingevents li a").focus(function() {
                    $(this).closest("li").addClass("hover");
                });

                 $(".hp-row.three .upcomingevents li a").focusout(function() {
                    $(this).closest("li").removeClass("hover");
                });

                $(".cs-article-slider-control.back").text("Prev");
                $(".cs-article-slider-control.next").text("Next");

                $(".hp-row.four .headlines.spotlight .more-link").prependTo(".hp-row.four .headlines.spotlight .ui-widget-footer");

				var numHeadlines = $(".hp-row.four .ui-widget.app.headlines:not(.spotlight)").find(".ui-article").length;
                $(".hp-row.four .ui-widget.app.headlines:not(.spotlight)").each(function(){



                    //SETUP FOOTER COLUMNS
                    $("


").appendTo($(this).find(".ui-widget-footer"));

                    //WRAP CONTROLS
                    $(this).find(".cs-article-slider-control").wrapAll("
");

                    //MOVE CONTROLS
                    $(this).find(".cs-article-slider-control-wrapper").prependTo($(this).find(".app-footer-column.two"));
                    $(this).find(".more-link").prependTo($(this).find(".app-footer-column.one"));
                    $(this).find(".app-level-social-rss").prependTo($(this).find(".app-footer-column.three"));

                    if($(".hp-row.four .headlines").length > 1){
                    	if(numHeadlines > 3){
                            //ADD PLAY / PAUSE BUTTONS
                            $("").insertBefore($(this).find(".cs-article-slider-control.next"));
                        }
                    } else {
                    	if(numHeadlines > 4){
                            //ADD PLAY / PAUSE BUTTONS
                            $("").insertBefore($(this).find(".cs-article-slider-control.next"));
                        }
                    }


                })

               //BEGIN AUTO SLIDE
               if($(".hp-row.four .headlines").length > 1){
                  if(numHeadlines > 3){
                      headlinesSlideInterval = setInterval(autoSlideHeadlines, 5000);
                      startTimeMS = '';
                      $(".cs-article-slider, .cs-article-slider-control-wrapper").addClass("playing");
                 }
               } else {
                  if(numHeadlines > 4){
                        headlinesSlideInterval = setInterval(autoSlideHeadlines, 5000);
                        startTimeMS = '';
                        $(".cs-article-slider, .cs-article-slider-control-wrapper").addClass("playing");
                   }
               }


                $(".cs-article-slider-control-custom.pause").on("click keydown", function(e) {

                    if(_this.AllyClick(e)) {
                        e.preventDefault();
                        stopAutoSlideHeadlines();
                        $(".cs-article-slider-control-custom.play").focus();
                    }
                });

                $(".cs-article-slider-control-custom.play").on("click keydown", function(e) {

                    if(_this.AllyClick(e)) {
                        e.preventDefault();
                        $(".cs-article-slider-control-wrapper, .cs-article-slider").removeClass("paused").addClass("playing");
                        $(".cs-article-slider-control.next").click();
                        headlinesSlideInterval = setInterval(autoSlideHeadlines, 5000);
                        $(".cs-article-slider-control-custom.pause").focus();
                    }
                });

                function autoSlideHeadlines(){
                    $(".cs-article-slider-control.next").click(); 
                    startTimeMS = (new Date()).getTime();
                }

                function stopAutoSlideHeadlines(){
                    $(".cs-article-slider-control-wrapper, .cs-article-slider").removeClass("playing").addClass("paused");
                    clearInterval(headlinesSlideInterval);
                }

                //PAUSE SLIDER WHEN AN ARTICLE HAS FOCUS
                $(".cs-article-slider .ui-article-title a").on("focus", function(){
                    stopAutoSlideHeadlines();
                    $(".cs-article-slider").addClass("stopped-focus");
                })

                //PLAY SLIDER WHEN FOCUS LEAVES ARTICLES (only if slide wasn't manually paused)
                $(".cs-article-slider-control.back").on("focus", function(){
                    if($(".cs-article-slider").hasClass("stopped-focus")){
                        $(".cs-article-slider-control-custom.play").click();
                        $(".cs-article-slider").removeClass("stopped-focus");
                    }
                })

                $(".cs-article-slider-control.next").click(function(){
                    currentTime = (new Date()).getTime();

                    //IF THE DIFFERENCE BETWEEN CurrentTime and startTimeMS IS < 5800, THEN WE KNOW A USER CLICKED THE CONTROL
                    if(currentTime - startTimeMS < ((3 * 1000) - 200)){
                        $(".cs-article-slider").addClass("user-click");
                        $(".cs-article-slider-notifications").attr("aria-live","assertive");
                    } else {
                        $(".cs-article-slider").removeClass("user-click");
                        $(".cs-article-slider-notifications").attr("aria-live","off");
                    }
                });

                //REGION C BACKGROUND IMAGE
                var regionCBGImgSrc = jQuery.trim("/cms/lib/CA50000164/Centricity/Template/GlobalAssets/images///background images/ocean.png");
                var regionCBGImgSrcSplit = regionCBGImgSrc.split("/");
                var regionCBGImgSrcSplitLen = regionCBGImgSrcSplit.length;
                if((regionCBGImgSrc != "") && (regionCBGImgSrcSplit[regionCBGImgSrcSplitLen - 1] != "default-man.jpg")) {
                    $(".hp-row.three").css("background-image", "url('"+regionCBGImgSrc+"')");
                } else {
                    $(".hp-row.three").css("background-image", "url('/cms/lib/CA50000164/Centricity/Template/17/defaults/region-c-background.jpg')");
                }
            }



        },

        "Shortcuts": function() {
            var _this = this;

            // EDIT THESE TWO VARS
            var columnNums = [4, 3, 2, 2, 1]; // [960, 768, 640, 480, 320]
            var selector = ".hp-row.three div.ui-widget.app.siteshortcuts, .hp-row.four:not(.spotlight) div.ui-widget.app.siteshortcuts";

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(_this.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $(selector).each(function() {
                // RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).append($(".site-shortcuts-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
                var shortcutNum = $(".ui-widget-detail > ul.site-shortcuts > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                    // KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                        // IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                        // WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN

                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                           endRange = Math.round(shortcutNum / columnNum) + 1;
                       } else if((shortcutNum / columnNum) % 1 == 0.5 && i >= (columnNum / i)) {
                           endRange = Math.round(shortcutNum / columnNum) - 1;
                       } else {
                           endRange = Math.round(shortcutNum / columnNum)
                       }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.site-shortcuts > li:nth-child(n+1):nth-child(-n+" + endRange +")", this));
                    }
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).hide();
            });
        },

        "Footer": function() {
            // FOR SCOPE
            var _this = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-bb-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-bb-footer.copyright").append($("#sw-footer-copyright").html());

            //CHECK IMPORTANT LINKS
            var impLinks = [true, true, true, true];

