    var delay = (function(){
      var timer = 0;
      return function(callback, ms){
        clearTimeout (timer);
        timer = setTimeout(callback, ms);
      };
    })();

    var search_page = instantsearch({
      searchFunction: function(helper) {
      if (helper.state.query === '') {
        return;
      }
      delay(function(){
      }, 1000 );
      helper.search();
