function resize_sp478() {
	var windowWidth = Math.max(parseFloat("646"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("0");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel478Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel478Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel478Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel478Div").style.height = Math.max((parseFloat("338")/parseFloat("646") * windowWidth), parseFloat("338")) + "px";
	document.getElementById("carousel478Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel478Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

