div.script:
$(function() {
	var $carousel=$("#post-carousel_B9A2A63F-5056-9010-351A4E3ACB38879D");
	$carousel.find(".post-carousel").each(function(event) {
		var that = this;
		var $link = $(this).find(".record-link");
		var videoLink = $(this).find(".record-image-link").attr("data-video-url");
		
		if(videoLink && videoLink.length) {
			$(this).find(".post-carousel-overlay").on("click", function(event) {
				event.preventDefault();
				$.magnificPopup.open({
					items: {
						src: videoLink
						, type: 'iframe'
					}
				});
			});
		} else if($link.length) {
			$(this).find(".post-carousel-overlay").on("click", function(event) {
				event.preventDefault();
				window.location = $link.attr("href");
			});
		}
	})
	$carousel.find(".post-carousels").owlCarousel({
		items: 1
		, itemsCustom: [[0, 1], ]
		, pagination: false
		, rewindSpeed: 200
		/* , rewindNav: false */
		, autoPlay: true
	});
	$carousel.find(".prev").click(function(){ $carousel.find(".post-carousels").trigger("owl.prev") });
	$carousel.find(".next").click(function(){ $carousel.find(".post-carousels").trigger("owl.next") });
});
