$(window).load(function() {
    $('span.featured-link-caption span').each(function (i, e) {
        var txt = '';
        for (var c = 0; c < e.childNodes.length; c++) {
            if (e.childNodes[c].nodeType === 3) {
                txt += e.childNodes[c].data;
            }
        }
        if (txt.trim().length === 0) {
            $(e).hide();
        }
    });
});
