$(".group868567").colorbox({rel:'group868567', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group868567").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery868567 = $("a[rel=group868567]").colorbox({maxWidth: "80%"});
$("a#openGallery868567").click(function(e){
    e.preventDefault();
    $gallery868567.eq(0).click();
});

