	$(function(){
		//DOC vars
		isSticky = 0;
		channelBarOffset = $("#gb-channel-list-outer").offset().top;
		channelBarOffset = channelBarOffset+$("#gb-channel-list-outer").height()+20;

		// SET BROWSER AND VERSION CLASS TO BODY
		$("body").addClass(browserName + " " + browserName + browserVersion);

		$(window).bind("resize", function(){ calendarListView(); pgScreenSize(); });

		// RESPONSIVE MENU
		$("body").csRsMenu({
		        "breakPoint"            : 640, //Changed from 768 by Marie - Case #00105472 // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left   
		        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
		        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
		        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
		        "menuButtonParent"      : "#sw-mystart-right",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "Menu",
		        "showSchools"           : "yes",
		        "showTranslate"         : "no",
		        "translateId"           : "none",
		        "showAccount"           : "yes",
		        "extraMenuOptions"      : "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
		        "extraMenuItems"        : "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
		        "siteID"                : "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left
		        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
		        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
