		});

		$("body").csRsSameHeights({
		        resizeElements : [
		                {
		                        "resizeMethod"          : "columns",            // OPTIONS - columns, apps
		                        "parent"                : "#sp-content",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
		                        "columns"               : ".sp.column",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
		                        "gutter"                : 0,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
		                        "applySameHeights"      : ["960", "768"]        // OPTIONS - ["960", "768", "640", "480", "320"]
		                }
		        ]
		});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();	
		addFramesetTranslate();
	});

	function pgScreenSize(){
		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			stickyChannelBar();
			$(window).scroll(stickyChannelBar);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
			$(window).unbind("scroll", stickyChannelBar);
			stickyChannelBarReset();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
		}
	}

	function photoGallery() {
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1280,
		                "imageHeight"             : 475,
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "yes",
		                "linkedElement"           : ["all"], // ["image", "title", "overlay", "all"]
		                "mainControls"            : "yes",
		                "descMoreLink"            : "yes",
		                "descMoreLinkText"        : "Read More",
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 1,
		                "transitionDelay"         : 7,
		                "onTransitionEnd"       : function() { 
		              		$(".rs-caption p .rs-gallery-desc-more-link").remove();
		              		var link = $(".rs-gallery-desc > .rs-gallery-desc-more-link a").attr("href");
		                	if(link.length > 0) {
		                		$(".rs-gallery-desc > .rs-gallery-desc-more-link").addClass("active");
		                		$(".rs-gallery-desc > .rs-gallery-desc-more-link").clone().appendTo($(".rs-caption p")); 
		                	}
		                },
		                "allLoaded"		  : function() { 
		                	var link = $(".rs-gallery-desc > .rs-gallery-desc-more-link a").attr("href");
               				if(link.length > 0) {
		                		$(".rs-gallery-desc > .rs-gallery-desc-more-link").addClass("active");
		                		$(".rs-gallery-desc > .rs-gallery-desc-more-link").clone().appendTo($(".rs-caption.active p")); 
		                	}
		                }	
		        });
		}
	}

	function appView() {
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length) {
			$("#hp-content div.ui-widget.app:eq(2)").addClass("active");
		}

		$("#hp-content div.ui-widget-header").click(function() {
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function useChannelForSection() {
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function dynamicContent() {
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron, .sw-dropdown-arrow").addClass("icon-font").html("");
		//DOC replace home icon 
		$("#sw-home-icon").addClass("icon-font").html("");
		//DOC shift school dropdown to front
		$(".sw-mystart-dropdown.schoollist").prependTo($("#sw-mystart-left"));
		//DOC change school dropdown text
		$(".sw-mystart-dropdown.schoollist .selector").text("Select a School");
		//DOC move signin, and site manager to #sw-mystart-left
		$(".sw-mystart-button.signin, .sw-mystart-button.register").appendTo($("#sw-mystart-left"));
		$(".sw-mystart-button.manage, #sw-mystart-account, #sw-mystart-mypasskey").appendTo($("#sw-mystart-left"));
		//DOC repalce search icon
