$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=aLqm5DxPAt4Fss5xQNX3Vg%3d%3d&SessionID=409f9410-a434-480a-b312-1ca7a868bda9&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+7%3a50%3a58+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.77.58';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
