div.script:
$(document).ready(function() {

    $('#carousel_1').on('slide.bs.carousel', function (e) {

        var nextImage;
        nextImage = $(e.relatedTarget).find('img');

        var src = nextImage.attr('data-lazy-load-src');
        var srcSet = nextImage.attr('data-lazy-load-srcset');

        if(typeof  srcSet !== "undefined" && srcSet != "") {
            nextImage.attr('srcset', srcSet)
            nextImage.attr('data-lazy-load-srcset', '');
        }

        if (typeof src !== "undefined" && src != "") {
            nextImage.attr('src', src)
            nextImage.attr('data-lazy-load-src', '');
        }

        $('.carousel-inner .item').attr('aria-hidden','true');
        $(e.relatedTarget).attr('aria-hidden','false');

    });

    $('#playButton').click(function () {
        $('#carousel_1').carousel('cycle');
    });

    $('#pauseButton').click(function () {
        $('#carousel_1').carousel('pause');
    });

    $('#stopButton').click(function () {
        $('#carousel_1').carousel('pause');
    });

    $('#previousButton').click(function () {
        $('#carousel_1').carousel('prev');
    });

    $('#nextButton').click(function () {
        $('#carousel_1').carousel('next');
    });

    $('#hideButton').click(function () {
        $('#carousel_1').hide();
        $('#carousel_1').attr('aria-hidden','true');
    });

    $("button").keypress(function(ev) {
        if (ev.which ==13) {
            $(this).click();
            ev.preventDefault();
        }
    });

});

window.NimbleCms.widget('calendar', function () {
    $('#upcoming-events-17 [data-event-id]').each(function (index, element) {
        var eventLink = $(this);
        eventLink.on('click', function () {
            $.get('/api/calendar/event/' + eventLink.attr('data-event-id'), {
                start: eventLink.attr('data-event-in-series') == 1 ? eventLink.attr('data-event-start-date') : null,
                end: eventLink.attr('data-event-in-series') == 1 ? eventLink.attr('data-event-end-date') : null
            }, function (event) {
                return Builder.modal(eventLink.html(), event, 'Close', 'btn-primary', 'modal-lg');
            });
        });
    });
});
