div.script:
$(document).ready(function () {
    $("#cbd112b9-9570-4320-89a1-7b808363ab8f").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#d14d80b6-0a28-410b-af11-052a8fe64898").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#d14d80b6-0a28-410b-af11-052a8fe64898').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#d14d80b6-0a28-410b-af11-052a8fe64898').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }

});

