        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'da130609-e5a8-4aa7-b0c8-b79ade4f498d',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

