	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		//$("#sw-mystart-left").appendTo("#sw-mystart-inner");
		$(".sw-myaccount-chevron").text("▼");
		$(".schoollist .selector").text("Select School");
		$(".schoollist").show();
		$("#sw-mystart-search").unbind();

		//Added by Marie - Case 00171809
		//$(".sw-mystart-dropdown.schoollist .sw-dropdown-list li").eq(54).css("display", "none");

		// ********** HEADER ********** //

		//DOC JOELS DICTIONARY BASED SCHOOLNAME SPLITTER
		var dict = ["of t", "elementary","middle","high","junior","senior","stem","academy","school"],
		    siteNameArray = ["West Ada School District"];
		for(var j=0;j -1) { 
				siteNameArray = [siteNameArray[0].slice(0,match-1),siteNameArray[0].slice(match-1)];
				break;
			};
		};

		for(var k=0;k siteNameArray[0]
				//$("#gb-sitename").prepend("
" + siteNameArray[0] + "");
				$("#footer-address .sitename").text(siteNameArray[0]);
			} else {
				//SECOND LINE TO DOM LOCATIONS -> siteNameArray[1]
				//$("#gb-sitename").append("
" + siteNameArray[1]+ "");
				$("#footer-address .sitename").append('' + siteNameArray[1] + '');
			}
		};

		var siteNameOne = jQuery.trim('');
		var siteNameTwo = jQuery.trim('');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "West Ada School District";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + " " + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + " " + siteNameTwo + "");
		}


		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
