$(".group867115").colorbox({rel:'group867115', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group867115").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery867115 = $("a[rel=group867115]").colorbox({maxWidth: "80%"});
$("a#openGallery867115").click(function(e){
    e.preventDefault();
    $gallery867115.eq(0).click();
});

